INSERT INTO `ddys_answer` VALUES (265, '您好，非常感谢您的信任和支持，您的资料正在详细阅读中，我马上详细语音回复您的消息，此条勿回复，以免占用您的回复次数，谢谢您的信任和合作。', '2020-09-26 09:57', 133, 51, 92);
